(function clickCircle(event){function getRandomColor(){const r=Math.floor(Math.random()*256);const g=Math.floor(Math.random()*256);const b=Math.floor(Math.random()*256);return`rgb(${r},${g},${b})`}$(document).on('click',function(event){const $circle=$('<div class="click-circle"></div>');$circle.css({left:`${event.clientX-5}px`,top:`${event.clientY-5}px`,borderColor:getRandomColor(),});$('body').append($circle);requestAnimationFrame(()=>{$circle.css({transform:'scale(6)',opacity:'0'})});setTimeout(()=>{$circle.remove()},600)})})();
// end click-circle.min.js